/**
 * Module dependencies.
 */

const passport = require('passport');
const express = require('express'); // or any other web framework you are using
const app = express(); // or your web framework's equivalent
// ... other imports and configurations ...
app.use(passport.initialize()); // Initialize Passport.js

var Passport = require('./authenticator')
  , SessionStrategy = require('./strategies/session');


/**
 * Export default singleton.
 *
 * @api public
 */
exports = module.exports = new Passport();

/**
 * Expose constructors.
 */
exports.Passport =
exports.Authenticator = Passport;
exports.Strategy = require('passport-strategy');

/**
 * Expose strategies.
 */
exports.strategies = {};
exports.strategies.SessionStrategy = SessionStrategy;
